import * as d3 from 'd3';

// Funzione per creare il grafico a dispersione
function createScatterplot(data) {
  // set the dimensions and margins of the graph
  const margin = { top: 10, right: 30, bottom: 30, left: 60 };
  const width = 700 - margin.left - margin.right;
  const height = 500 - margin.top - margin.bottom;

  // Creazione della scala X
  const x = d3.scaleLinear()
    .domain([0, d3.max(data, d => +d.hourlyRate)])
    .range([0, width]);

  // Creazione della scala Y
  const y = d3.scaleLinear()
    .domain([0, d3.max(data, d => +d.counts)])
    .range([height, 0]);

  // Creazione dell'asse X
  const xAxis = d3.axisBottom(x);
  svg.append('g')
    .attr('transform', `translate(0, ${height})`)
    .call(xAxis);

  // Creazione dell'asse Y
  const yAxis = d3.axisLeft(y);
  svg.append('g')
    .call(yAxis);

  // Aggiunta dei punti
  const dots = svg.selectAll('circle')
    .data(data)
    .enter()
    .append('circle')
    .attr('cx', d => x(+d.hourlyRate))
    .attr('cy', d => y(+d.counts))
    .attr('r', 5)
    .style('fill', 'gray')
    .on('mouseover', function (event, d) {
      // Cambia il colore dei punti con lo stesso jobId
      const jobId = d.jobId;
      dots.style('fill', dotData => (dotData.jobId === jobId ? getRandomColor() : 'gray'));
    })
    .on('mouseout', function () {
      // Ripristina il colore grigio per tutti i punti
      dots.style('fill', 'gray');
    });
}

// Funzione per ottenere un colore casuale
function getRandomColor() {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

// Richiesta dei dati CSV in Vue.js
export default {
  mounted() {
    d3.csv('turnover.csv').then(data => {
      createScatterplot(data);
    });
  },
};
