import * as d3 from 'd3';

export function TrafficPlot(data) {
    const parsedData = data.map((d) => {
      const startDate = new Date(d.travelStartTime);
      const endDate = new Date(d.travelEndTime);
      const travelTime = (endDate - startDate) / (1000 * 60);
      return { startDate, endDate, travelTime };
    });

    const weekStart = new Date(parsedData[0].startDate.getFullYear(), parsedData[0].startDate.getMonth(), parsedData[0].startDate.getDate() - parsedData[0].startDate.getDay());
    const weekEnd = new Date(parsedData[parsedData.length - 1].startDate.getFullYear(), parsedData[parsedData.length - 1].startDate.getMonth(), parsedData[parsedData.length - 1].startDate.getDate() + 6);
    const totalWeeks = Math.ceil((weekEnd - weekStart) / (1000 * 60 * 60 * 24 * 7));
    this.totalWeeks = totalWeeks;
    this.weekStart = weekStart;

    const margin = { top: 20, right: 20, bottom: 30, left: 40 };
    const width = 800 - margin.left - margin.right;
    const height = 400 - margin.top - margin.bottom;

    const svg = d3
      .select('#chartContainer')
      .append("svg")
      .attr("width", width + margin.left + margin.right)
      .attr("height", height + margin.top + margin.bottom);

    this.chart = svg.append("g").attr("transform", `translate(${margin.left},${margin.top})`);

    const updateChart = () => {
      const currentWeekStart = new Date(this.weekStart.getFullYear(), this.weekStart.getMonth(), this.weekStart.getDate() + (this.currentWeekIndex * 7));
      const currentWeekEnd = new Date(currentWeekStart.getFullYear(), currentWeekStart.getMonth(), currentWeekStart.getDate() + 6);
      const currentWeekData = parsedData.filter((d) => {
        return d.startDate >= currentWeekStart && d.startDate <= currentWeekEnd;
      });
      const maxTravelTime = d3.max(currentWeekData, (d) => d.travelTime);
      this.maxTravelTime = maxTravelTime;

      const weekdays = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
      const totalTravelTime = parsedData.reduce((total, d) => total + d.travelTime, 0);
      this.totalTravelTime = totalTravelTime;
      const totalTravelTimes = weekdays.map((day) => {
        const dayTravelTimes = currentWeekData.filter((d) => d.startDate.getDay() === weekdays.indexOf(day));
        const totalTravelTime = d3.sum(dayTravelTimes, (d) => d.travelTime);
        return { day, totalTravelTime };
      });

      svg.selectAll(".bar").remove();
      svg.selectAll(".x-axis").remove();
      svg.selectAll(".y-axis").remove();

      const x = d3.scaleBand().range([0, width]).padding(0.1).domain(weekdays);
      const y = d3.scaleLinear().range([height, 0]).domain([0, maxTravelTime]);

      chart.append("g").attr("class", "x-axis").attr("transform", `translate(0,${height})`).call(d3.axisBottom(x));
      chart.append("g").attr("class", "y-axis").call(d3.axisLeft(y));

      this.chart
        .selectAll(".bar")
        .data(totalTravelTimes)
        .enter()
        .append("rect")
        .attr("class", "bar")
        .attr("x", (d) => x(d.day))
        .attr("y", (d) => y(d.totalTravelTime))
        .attr("width", x.bandwidth())
        .attr("height", (d) => height - y(d.totalTravelTime));
    };

    updateChart();

    this.nextWeek = () => {
      if (this.currentWeekIndex < this.totalWeeks - 1) {
        this.currentWeekIndex++;
        updateChart();
      }
    };

    this.previousWeek = () => {
      if (this.currentWeekIndex > 0) {
        this.currentWeekIndex--;
        updateChart();
      }
    }
}
