import * as d3 from "d3";

// M A P
function BuildingMap() {
  const color = "#ffffff";
  let extent;

  function me(selection) {
    const buildingTypes = d3
      .groups(selection.datum().map(d => d.properties.buildingType), d => d)
      .map(d => d[0]);
    console.log("buildingTypes", buildingTypes);

    extent = {
      type: "GeometryCollection",
      geometries: selection.datum().map(d => d.geometry)
    };

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const path = d3.geoPath().projection(projection);

    selection
      .selectAll("path")
      .data(selection.datum())
      .join("path")
      .attr("d", path)
      .attr("fill", color)
      .style("stroke", "black");
  }

  me.color = function () {
    return color;
  };

  me.extent = function() {
    return extent
  };

  return me;
}

// A P A R T M E N T S
function BuildingApt() {
    function me(selection, extent) {
      const apartmentIds = d3
        .groups(selection.datum().map(d => d.properties.apartmentId), d => d)
        .map(d => d[0]);

      console.log("apartmentIds", apartmentIds);

      const projection = d3
        .geoIdentity()
        .reflectY(true)
        .fitSize([650, 650], extent);

      selection
        .selectAll(".apt")
        .data(selection.datum())
        .join("circle")
        .attr("class", "apt")
        .attr("cx", d => projection(d.geometry.coordinates)[0])
        .attr("cy", d => projection(d.geometry.coordinates)[1])
        .attr("r", 1);
    }

    return me;
  }
// P U B S
function BuildingPub() {
  function me(selection, extent) {
    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    selection
      .selectAll(".pub")
      .data(selection.datum())
      .join("circle")
      .attr("class", "pub")
      .attr("cx", d => projection(d.geometry.coordinates)[0])
      .attr("cy", d => projection(d.geometry.coordinates)[1])
      .attr("r", d => Math.sqrt(d.properties.hourlyCost) * 5)
      .style("fill", "transparent")
      .style("stroke", "black")
      .on("mouseover", (event, d) => {
        const hourlyCost = d.properties.hourlyCost;
        tooltip
          .style("opacity", 1)
          .html(`Hourly cost: $${hourlyCost.toFixed(2)}`)
          .style("left", event.pageX + "px")
          .style("top", event.pageY + "px");
      })
      .on("mouseout", () => {
        tooltip.style("opacity", 0);
      });
  }

  return me;
}

// R E S T A U R A N T S
function BuildingRestaurant() {
  function me(selection, extent) {
    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    selection
      .selectAll(".restaurant")
      .data(selection.datum())
      .join("circle")
      .attr("class", "restaurant")
      .attr("cx", d => projection(d.geometry.coordinates)[0])
      .attr("cy", d => projection(d.geometry.coordinates)[1])
      .attr("r", d => Math.sqrt(d.properties.foodCost) * 5)
      .style("fill", "transparent")
      .style("stroke", "black")
      .on("mouseover", (event, d) => {
        const foodCost = d.properties.foodCost;
        tooltip
          .style("opacity", 1)
          .html(`Food cost: $${foodCost.toFixed(2)}`)
          .style("left", event.pageX + "px")
          .style("top", event.pageY + "px");
      })
      .on("mouseout", () => {
        tooltip.style("opacity", 0);
      });
  }

  return me;
}

export { BuildingMap, BuildingApt, BuildingPub, BuildingRestaurant };
