import * as d3 from 'd3';

export function createScatterplot(svgElement, tooltipElement, data) {
  // set the dimensions and margins of the graph
  const margin = { top: 10, right: 30, bottom: 30, left: 60 };
  const width = 700 - margin.left - margin.right;
  const height = 500 - margin.top - margin.bottom;

  // append the svg object to the component element
  const svg = d3.select(svgElement)
    .attr('width', width + margin.left + margin.right)
    .attr('height', height + margin.top + margin.bottom)
    .append('g')
    .attr('transform', `translate(${margin.left}, ${margin.top})`);

  // Creazione della scala X
  const x = d3.scaleLinear()
    .domain([0, d3.max(data, d => +d.hourlyRate) + 10])
    .range([0, width]);

  // Creazione della scala Y
  const y = d3.scaleLinear()
    .domain([0, d3.max(data, d => +d.counts) + 5])
    .range([height, 0]);

  // Creazione dell'asse X
  const xAxis = d3.axisBottom(x).tickValues(d3.range(0, d3.max(data, d => +d.hourlyRate) + 11, 10));
  svg.append('g')
    .attr('transform', `translate(0, ${height})`)
    .call(xAxis);

  // Creazione dell'asse Y
  const yAxis = d3.axisLeft(y).tickValues(d3.range(0, d3.max(data, d => +d.counts) + 6, 5));
  svg.append('g')
    .call(yAxis);

  // Aggiunta dei punti
  svg.selectAll('circle')
    .data(data)
    .enter()
    .append('circle')
    .attr('cx', d => x(+d.hourlyRate))
    .attr('cy', d => y(+d.counts))
    .attr('r', 5)
    .style('fill', 'gray')
    .style('stroke', 'gray')
    .on('mouseover', function (event, d) {
      // Evidenzia il pallino
      d3.select(this).style('fill', 'red');

      // Mostra il tooltip con le informazioni del punto
      const tooltip = d3.select(tooltipElement);
      tooltip.transition().duration(200).style('opacity', 1);

      const xPosition = event.clientX;
      const yPosition = event.clientY;

      tooltip.html(`Job ID: ${d.jobId}<br>Turnover: ${d.counts}<br>Hourly wage: ${d.hourlyRate}`)
        .style('left', `${xPosition-325}px`)
        .style('top', `${yPosition + 500}px`); // Adjust the y position

      // Aggiungi le linee tratteggiate
      svg.append('line')
        .attr('x1', x(+d.hourlyRate))
        .attr('y1', height)
        .attr('x2', x(+d.hourlyRate))
        .attr('y2', y(+d.counts))
        .style('stroke', 'gray')
        .style('stroke-dasharray', '2,2');

      svg.append('line')
        .attr('x1', 0)
        .attr('y1', y(+d.counts))
        .attr('x2', x(+d.hourlyRate))
        .attr('y2', y(+d.counts))
        .style('stroke', 'gray')
        .style('stroke-dasharray', '2,2');
    })
    .on('mouseout', function () {
      // Ripristina il colore grigio per il pallino
      d3.select(this).style('fill', 'gray');

      // Nascondi il tooltip
      const tooltip = d3.select(tooltipElement);
      tooltip.transition().duration(500).style('opacity', 0);

      // Rimuovi le linee tratteggiate
      svg.selectAll('line').remove();
    });
}
