import { BCard } from 'bootstrap-vue';
import * as d3 from 'd3';
export default {
  name: 'App',
  components: {
    BCard
  },
  mounted() {
    this.drawScatterPlot();
  },
  methods: {
    drawScatterPlot() {
      const data = [{
        id: 1,
        age: 25,
        householdSize: 2
      }, {
        id: 2,
        age: 30,
        householdSize: 3
      }, {
        id: 3,
        age: 35,
        householdSize: 4
      }, {
        id: 4,
        age: 40,
        householdSize: 2
      }, {
        id: 5,
        age: 45,
        householdSize: 5
      }, {
        id: 6,
        age: 50,
        householdSize: 2
      }, {
        id: 7,
        age: 55,
        householdSize: 3
      }, {
        id: 8,
        age: 60,
        householdSize: 4
      }, {
        id: 9,
        age: 65,
        householdSize: 2
      }, {
        id: 10,
        age: 70,
        householdSize: 5
      }];
      const margin = {
        top: 20,
        right: 20,
        bottom: 30,
        left: 50
      };
      const width = 600 - margin.left - margin.right;
      const height = 400 - margin.top - margin.bottom;
      const svg = d3.select(this.$refs.scatterplot).append('svg').attr('width', width + margin.left + margin.right).attr('height', height + margin.top + margin.bottom).append('g').attr('transform', `translate(${margin.left},${margin.top})`);
      const x = d3.scaleLinear().domain([20, 75]).range([0, width]);
      const y = d3.scaleLinear().domain([1, 5]).range([height, 0]);
      svg.append('g').attr('transform', `translate(0,${height})`).call(d3.axisBottom(x));
      svg.append('g').call(d3.axisLeft(y));
      const circles = svg.selectAll('circle').data(data).enter().append('circle').attr('cx', d => x(d.age)).attr('cy', d => y(d.householdSize)).attr('r', 5).style('fill', 'steelblue');
      circles.on('mouseover', function (event, d) {
        d3.select(this).style('fill', 'orange');
        const tooltip = svg.append('text').attr('class', 'tooltip').attr('x', x(d.age) + 10).attr('y', y(d.householdSize) - 10).text(`Participant ${d.id}: Age ${d.age}, Household Size ${d.householdSize}`);
      }).on('mouseout', function (event, d) {
        d3.select(this).style('fill', 'steelblue');
        svg.select('.tooltip').remove();
      });
    }
  }
};
