import * as d3 from 'd3';
import { format, startOfWeek, endOfWeek, eachDayOfInterval } from 'date-fns';

export function createBarChart(data) {
  // Impostazioni del grafico
  var margin = { top: 20, right: 20, bottom: 30, left: 40 };
  var width = 800 - margin.left - margin.right;
  var height = 400 - margin.top - margin.bottom;

  // Creazione dello scale per gli assi
  var x = d3.scaleBand()
    .range([0, width])
    .padding(0.1);

  var y = d3.scaleLinear()
    .range([height, 0]);

  // Creazione dell'elemento SVG
  var svg = d3.select("#chart")
    .attr("width", width + margin.left + margin.right)
    .attr("height", height + margin.top + margin.bottom)
    .append("g")
    .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

  // Funzione per aggiornare il grafico
  function updateChart(startDate) {
    // Calcola la data di fine della settimana
    var endDate = endOfWeek(startDate);

    // Genera l'array di date per la settimana selezionata
    var weekDates = eachDayOfInterval({ start: startDate, end: endDate });

    // Filtra i dati per la settimana selezionata
    var filteredData = data.filter(function (d) {
      var date = new Date(d.date);
      return weekDates.includes(date);
    });

    // Aggiorna il dominio degli assi
    var weekdays = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
    x.domain(weekdays);
    y.domain([0, d3.max(filteredData, function (d) { return d.travelDuration; })]);

    // Rimuovi eventuali barre precedenti
    svg.selectAll(".bar").remove();

    // Aggiungi le nuove barre
    svg.selectAll(".bar")
      .data(filteredData)
      .enter().append("rect")
      .attr("class", "bar")
      .attr("x", function (d) { return x(weekdays[new Date(d.date).getDay()]); })
      .attr("width", x.bandwidth())
      .attr("y", function (d) { return y(d.travelDuration); })
      .attr("height", function (d) { return height - y(d.travelDuration); });

    // Aggiorna l'asse x
    svg.select(".x-axis").call(d3.axisBottom(x));
  }

  // Inizializza il grafico con la prima settimana
  var initialStartDate = startOfWeek(d3.min(data, function (d) { return new Date(d.date); }));
  updateChart(initialStartDate);

  // Aggiungi asse x
  svg.append("g")
    .attr("class", "x-axis")
    .attr("transform", "translate(0," + height + ")")
    .call(d3.axisBottom(x));

  // Esporta la funzione di aggiornamento per poterla utilizzare nell'oggetto Vue
  return updateChart;
}
