  svg.append('g').call(yAxis);
  const circles = svg.selectAll('circle').data(data).enter().append('circle').attr('cx', d => x(+d.hourlyRate)).attr('cy', d => y(+d.counts)).attr('r', 5).style('fill', 'gray').style('stroke', 'darkgray');
  circles.on('mouseover', function (event, d) {
    d3.select(this).style('fill', 'red');
    const tooltip = d3.select("#turnover").append("div").attr("class", "tooltip").style("opacity", 0);
