import * as d3 from "d3";

// M A P
function BuildingMap() {
  const color = "#ffffff";
  let extent;

  function me(selection) {
    const buildingTypes = d3
      .groups(selection.datum().map(d => d.properties.buildingType), d => d)
      .map(d => d[0]);
    console.log("buildingTypes", buildingTypes);

    extent = {
      type: "GeometryCollection",
      geometries: selection.datum().map(d => d.geometry)
    };

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const path = d3.geoPath().projection(projection);

    selection
      .selectAll("path")
      .data(selection.datum())
      .join("path")
      .attr("d", path)
      .attr("fill", color)
      .style("stroke", "grey");
  }

  me.color = function () {
    return color;
  };

  me.extent = function() {
    return extent
  };

  return me;
}
function BuildingPub() {
  const colorScale = d3.scaleThreshold()
    .domain([8, 10, 12])
    .range(["#f1f1f1", "#a6bddb", "#2b8cbe", "#045a8d"]);

  function me(selection, extent) {
    const pubIds = d3
      .groups(selection.datum().map(d => d.properties.location), d => d)
      .map(d => d[0]);

    console.log("pubIds", pubIds);

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    // Aggiungi un elemento SVG per la legenda
    const legendSvg = selection.append("svg")
      .attr("width", 400)
      .attr("height", 50);

    // Crea un array con i range di valori e i colori corrispondenti per la legenda
    const legendData = [
      { range: "< 8", color: "#f1f1f1" },
      { range: "8 - 9.99", color: "#a6bddb" },
      { range: "10 - 12", color: "#2b8cbe" },
      { range: "> 12", color: "#045a8d" }
    ];

    // Calcola la larghezza di ogni elemento della legenda in base alla lunghezza del testo
    const legendItemWidths = legendData.map(d => getTextWidth(d.range, "legend-label") + 30);

    // Calcola la posizione x di ogni elemento della legenda in base alla larghezza degli elementi precedenti
    const legendItemPositions = [0];
    for (let i = 1; i < legendItemWidths.length; i++) {
      legendItemPositions.push(legendItemPositions[i - 1] + legendItemWidths[i - 1]);
    }

    // Aggiungi i rettangoli colorati nella legenda
    legendSvg.selectAll(".legend-rect")
      .data(legendData)
      .enter()
      .append("rect")
      .attr("class", "legend-rect")
      .attr("x", (d, i) => legendItemPositions[i] + 10)
      .attr("y", 10)
      .attr("width", (d, i) => legendItemWidths[i])
      .attr("height", 20)
      .attr("fill", d => d.color);

    // Aggiungi le etichette per i range di valori corrispondenti nella legenda
    legendSvg.selectAll(".legend-label")
      .data(legendData)
      .enter()
      .append("text")
      .attr("class", "legend-label")
      .attr("x", (d, i) => legendItemPositions[i] + legendItemWidths[i] / 2 + 10)
      .attr("y", 45)
      .text(d => d.range)
      .attr("text-anchor", "middle");

    selection
      .selectAll(".pub")
      .data(selection.datum())
      .join("circle")
      .attr("class", "pub")
      .attr("cx", d => projection(d.geometry.coordinates)[0])
      .attr("cy", d => projection(d.geometry.coordinates)[1])
      .attr("r", 10)
      .attr("fill", d => {
        const hourlyCost = d.properties.hourlyCost;
        if (hourlyCost < 8) {
          return colorScale(0);
        } else if (hourlyCost >= 8 && hourlyCost < 10) {
          return colorScale(8);
        } else if (hourlyCost >= 10 && hourlyCost <= 12) {
          return colorScale(10);
        } else {
          return colorScale(12);
        }
      })
      .style("stroke", "black")
      .on("mouseover", (event, d) => {
        const hourlyCost = d.properties.hourlyCost;
        tooltip
          .style("opacity", 1)
          .html(`Hourly cost: $ ${d3.format(".2f")(hourlyCost)}`)
          .style("left", event.pageX + "px")
          .style("top", event.pageY + "px");
      })
      .on("mouseout", () => {
        tooltip.style("opacity", 0);
      });
  }

  return me;
}

// Funzione di utilità per calcolare la larghezza del testo
function getTextWidth(text, className) {
  const container = d3.select("body").append("div").attr("class", className).text(text);
  const width = container.node().getBoundingClientRect().width;
  container.remove();
  return width;
}





function BuildingRestaurant() {
  const colorScale = d3.scaleDiverging(d3.interpolateReds);

  function me(selection, extent) {
    const restaurantData = selection.datum();

    const restaurantId = d3
      .groups(restaurantData, d => d.properties.restaurantId)
      .map(d => ({
        restaurantId: d[0],
        totalFoodCost: d3.sum(d[1], d => d.properties.foodCost)
      }));

    console.log("restaurantData", restaurantData);
    console.log("restaurantId", restaurantId);

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const foodCostExtent = d3.extent(restaurantData, d => d.properties.foodCost);
    colorScale.domain(foodCostExtent);

    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    selection
      .selectAll(".restaurant")
      .data(restaurantData)
      .join("circle")
      .attr("class", "restaurant")
      .attr("cx", d => projection(d.geometry.coordinates)[0])
      .attr("cy", d => projection(d.geometry.coordinates)[1])
      .attr("r", 10)
      .attr("fill", d => colorScale(d.properties.foodCost))
      .style("stroke", "black")
      .on("mouseover", (event, d) => {
        const foodCost = d.properties.foodCost;
        tooltip
          .style("opacity", 1)
          .html(`Food cost: $ ${d3.format(".2f")(foodCost)}`)
          .style("left", event.pageX + "px")
          .style("top", event.pageY + "px");
      })
      .on("mouseout", () => {
        tooltip.style("opacity", 0);
      });
  }

  return me;
}

export { BuildingMap, BuildingPub, BuildingRestaurant };
