import { getSingleEndpoint } from "../assets/apiConnector";
const d3 = require("d3");
export default {
  mounted() {},
  methods: {
    fetchTravelJournalData() {
      getSingleEndpoint({
        participantId: 2
      }, 'ParticipantStatusLogs').then(response => {
        const updatedData = response.data.map(d => ({
          timestamp: new Date(d.timestamp),
          currentMode: d.currentMode
        }));

        // Update the data and redraw the heatmap
        this.participantStatus = updatedData; // Rename from 'partecipanStatus' to 'participantStatus'
        this.drawHeatMap();
      });
    },
    drawHeatMap() {
      // Select the existing SVG element if it exists, or create a new one
      let svg = d3.select(this.$refs.heatmapContainer);
      if (svg.empty()) {
        // Create an SVG element for the heatmap
        svg = d3.select(this.$refs.heatmapContainer.parentNode).append('svg').attr('width', 650).attr('height', 700);
      }

      // Extract the necessary data for the heatmap (timestamps and current mode)
      const heatmapData = this.participantStatus.map(entry => ({
        timestamp: new Date(entry.timestamp.toDateString()),
        mode: entry.currentMode
      }));

      // Get the unique modes in the data
      const uniqueModes = [...new Set(heatmapData.map(d => d.mode))];

      // Define the color scale for the heatmap based on the unique modes
      const colorScale = d3.scaleOrdinal().domain(uniqueModes).range(d3.schemeCategory10);

      // Calculate the height of each row
      const rowHeight = 700 / heatmapData.length;

      // Update the heatmap rectangles
      const rects = svg.selectAll('rect').data(heatmapData);
      rects.enter().append('rect').merge(rects).attr('x', (_, i) => i * rowHeight).attr('y', d => {
        const dayStart = new Date(d.timestamp);
        dayStart.setHours(0, 0, 0, 0);
        const dayEnd = new Date(d.timestamp);
        dayEnd.setHours(23, 59, 59, 999);
        const yScale = d3.scaleTime().domain([dayStart, dayEnd]).range([0, 650]);
        return yScale(d.timestamp);
      }).attr('width', rowHeight).attr('height', d => {
        const dayStart = new Date(d.timestamp);
        dayStart.setHours(0, 0, 0, 0);
        const dayEnd = new Date(d.timestamp);
        dayEnd.setHours(23, 59, 59, 999);
        const yScale = d3.scaleTime().domain([dayStart, dayEnd]).range([0, 650]);
        return yScale(dayEnd) - yScale(dayStart);
      }).style('fill', d => colorScale(d.mode));
      rects.exit().remove();

      // Add X-axis
      svg.selectAll('.x-axis').remove();
      const xScale = d3.scaleTime().domain(d3.extent(heatmapData, d => d.timestamp)).range([0, 650]);
      const xAxis = d3.axisBottom(xScale).ticks(d3.timeHour.every(3)); // Specifica gli intervalli di 3 ore
      svg.append('g').attr('class', 'x-axis').attr('transform', 'translate(0, 700)').call(xAxis);

      // Add X-axis label
      svg.selectAll('.x-axis-label').remove();
      svg.append('text').attr('class', 'x-axis-label').attr('x', 650 / 2).attr('y', 700 - rowHeight / 2 + 40).attr('text-anchor', 'middle').text('Timestamp');

      // Add Y-axis
      svg.selectAll('.y-axis').remove();
      const yScale = d3.scaleBand().domain(heatmapData.map(d => d.mode)).range([0, 700]);
      svg.append('g').attr('class', 'y-axis').attr('transform', 'translate(40, 0)').call(d3.axisLeft(yScale));

      // Add Y-axis label
      svg.selectAll('.y-axis-label').remove();
      svg.append('text').attr('class', 'y-axis-label').attr('x', rowHeight / 2 + 40).attr('y', 700 / 2).attr('text-anchor', 'middle').attr('transform', 'rotate(-90)').text('Modes');

      // Add color legend
      const legend = svg.append('g').attr('class', 'legend').attr('transform', 'translate(550, 660)');
      const legendItems = legend.selectAll('.legend-item').data(uniqueModes).enter().append('g').attr('class', 'legend-item').attr('transform', (_, i) => `translate(-20, ${i * 20})`);
      legendItems.append('rect').attr('width', 10).attr('height', 10).attr('fill', d => colorScale(d));
      legendItems.append('text').attr('x', 15).attr('y', 8).text(d => d);

      // Update the width and height attributes of the SVG element
      svg.attr('width', 650).attr('height', 700);
    }
  }
};
