import * as d3 from "d3";

// M A P
function BuildingMap() {
  const color = "#ffffff";
  let extent;

  function me(selection) {
    const buildingTypes = d3
      .groups(selection.datum().map(d => d.properties.buildingType), d => d)
      .map(d => d[0]);
    console.log("buildingTypes", buildingTypes);

    extent = {
      type: "GeometryCollection",
      geometries: selection.datum().map(d => d.geometry)
    };

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const path = d3.geoPath().projection(projection);

    selection
      .selectAll("path")
      .data(selection.datum())
      .join("path")
      .attr("d", path)
      .attr("fill", color)
      .style("stroke", "black");
  }

  me.color = function () {
    return color;
  };

  me.extent = function() {
    return extent
  };

  return me;
}

// A P A R T M E N T S
function BuildingApt() {
    function me(selection, extent) {
      const apartmentIds = d3
        .groups(selection.datum().map(d => d.properties.apartmentId), d => d)
        .map(d => d[0]);

      console.log("apartmentIds", apartmentIds);

      const projection = d3
        .geoIdentity()
        .reflectY(true)
        .fitSize([650, 650], extent);

      selection
        .selectAll(".apt")
        .data(selection.datum())
        .join("circle")
        .attr("class", "apt")
        .attr("cx", d => projection(d.geometry.coordinates)[0])
        .attr("cy", d => projection(d.geometry.coordinates)[1])
        .attr("r", 1);
    }

    return me;
  }

// P U B S
function BuildingPub() {
  const colorScale = d3.scaleDiverging(d3.interpolateBlues);
  let pubActive = false; // Aggiunta variabile per tenere traccia dello stato del pulsante "pubs"

  function me(selection, extent) {
    const pubIds = d3
      .groups(selection.datum().map(d => d.properties.location), d => d)
      .map(d => d[0]);

    console.log("pubIds", pubIds);

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const hourlyCostExtent = d3.extent(selection.datum(), d => d.properties.hourlyCost);
    colorScale.domain(hourlyCostExtent);

    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    selection
      .selectAll(".pub")
      .data(selection.datum())
      .join("circle")
      .attr("class", "pub")
      .attr("cx", d => projection(d.geometry.coordinates)[0])
      .attr("cy", d => projection(d.geometry.coordinates)[1])
      .attr("r", 10)
      .attr("fill", d => colorScale(d.properties.hourlyCost))
      .style("stroke", "black")
      .on("mouseover", (event, d) => {
        if (pubActive) { // Verifica se il pulsante "pubs" è attivo
          const hourlyCost = d.properties.hourlyCost;
          tooltip
            .style("opacity", 1)
            .html(`Hourly cost: ${d3.format(".2f")(hourlyCost)}`)
            .style("left", event.pageX + "px")
            .style("top", event.pageY + "px");
        }
      })
      .on("mouseout", () => {
        tooltip.style("opacity", 0);
      });
  }

  me.togglePubActive = function () {
    pubActive = !pubActive;
  };

  return me;
}

// R E S T A U R A N T S
function BuildingRestaurant() {
  const colorScale = d3.scaleDiverging(d3.interpolateReds);
  let restaurantActive = false; // Aggiunta variabile per tenere traccia dello stato del pulsante "restaurant"

  function me(selection, extent) {
    const restaurantData = selection.datum();

    const restaurantId = d3
      .groups(restaurantData, d => d.properties.restaurantId)
      .map(d => ({
        restaurantId: d[0],
        totalFoodCost: d3.sum(d[1], d => d.properties.foodCost)
      }));

    console.log("restaurantData", restaurantData);
    console.log("restaurantId", restaurantId);

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const foodCostExtent = d3.extent(restaurantData, d => d.properties.foodCost);
    colorScale.domain(foodCostExtent);

    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    selection
      .selectAll(".restaurant")
      .data(restaurantData)
      .join("circle")
      .attr("class", "restaurant")
      .attr("cx", d => projection(d.geometry.coordinates)[0])
      .attr("cy", d => projection(d.geometry.coordinates)[1])
      .attr("r", 10)
      .attr("fill", d => colorScale(d.properties.foodCost))
      .style("stroke", "black")
      .on("mouseover", (event, d) => {
        if (restaurantActive) { // Verifica se il pulsante "restaurant" è attivo
          const foodCost = d.properties.foodCost;
          tooltip
            .style("opacity", 1)
            .html(`Food cost: ${d3.format(".2f")(foodCost)}`)
            .style("left", event.pageX + "px")
            .style("top", event.pageY + "px");
        }
      })
      .on("mouseout", () => {
        tooltip.style("opacity", 0);
      });
  }

  me.toggleRestaurantActive = function () {
    restaurantActive = !restaurantActive;
  };

  return me;
}



export { BuildingMap, BuildingApt, BuildingPub, BuildingRestaurant };
