import * as d3 from 'd3';
import { timeFormat } from 'd3-time-format';

export function drawChart(data, chartContainer) {
  const margin = { top: 20, right: 20, bottom: 30, left: 40 };
  const width = 600 - margin.left - margin.right;
  const height = 400 - margin.top - margin.bottom;

  const meanTravelDurationSeconds = d3.mean(data, (d) => +d.travelDuration);
  const meanTravelDurationHours = meanTravelDurationSeconds / 3600;
  const maxYValue = Math.ceil(meanTravelDurationHours) + 2;

  const svg = d3
    .select(chartContainer)
    .append('svg')
    .attr('width', width + margin.left + margin.right)
    .attr('height', height + margin.top + margin.bottom)
    .append('g')
    .attr('transform', `translate(${margin.left},${margin.top})`);

  const xScale = d3
    .scaleBand()
    .range([0, width])
    .padding(0.1)
    .domain(data.map((d) => getDayOfWeek(d.date)));

  const yScale = d3
    .scaleLinear()
    .range([height, 0])
    .domain([0, maxYValue]);

  svg
    .append('g')
    .attr('class', 'x-axis')
    .attr('transform', `translate(0,${height})`)
    .call(d3.axisBottom(xScale));

  svg.append('g').attr('class', 'y-axis').call(d3.axisLeft(yScale).ticks(5).tickFormat(d3.format('.0f')));

  svg
    .selectAll('.bar')
    .data(data)
    .enter()
    .append('rect')
    .attr('class', 'bar')
    .attr('x', (d) => xScale(getDayOfWeek(d.date)))
    .attr('y', (d) => yScale(+d.travelDuration / 3600))
    .attr('width', xScale.bandwidth())
    .attr('height', (d) => height - yScale(+d.travelDuration / 3600));
}

export function updateChart(data, selectedWeek, chartContainer) {
  const weekData = data.slice(selectedWeek * 7, selectedWeek * 7 + 7);
  const svg = d3.select(chartContainer).select('svg');

  const margin = { top: 20, right: 20, bottom: 30, left: 40 };
  const width = svg.attr('width') - margin.left - margin.right;
  const height = svg.attr('height') - margin.top - margin.bottom;

  const xScale = d3
    .scaleBand()
    .range([0, width])
    .padding(0.1)
    .domain(weekData.map((d) => getDayOfWeek(d.date)));

  const meanTravelDurationSeconds = d3.mean(data, (d) => +d.travelDuration);
  const meanTravelDurationHours = meanTravelDurationSeconds / 3600;
  const maxYValue = Math.ceil(meanTravelDurationHours) + 2;

  const yScale = d3
    .scaleLinear()
    .range([height, 0])
    .domain([0, maxYValue]);

  svg.select('.x-axis').call(d3.axisBottom(xScale));
  svg.select('.y-axis').call(d3.axisLeft(yScale).ticks(5).tickFormat(d3.format('.0f')));

  const bars = svg.selectAll('.bar').data(weekData, (d) => d.date);

  bars.exit().remove();

  bars
    .enter()
    .append('rect')
    .attr('class', 'bar')
    .merge(bars)
    .attr('x', (d) => margin.left + xScale(getDayOfWeek(d.date)))
    .attr('y', (d) => margin.top + yScale(+d.travelDuration / 3600))
    .attr('width', xScale.bandwidth())
    .attr('height', (d) => height - yScale(+d.travelDuration / 3600));
}

function getDayOfWeek(dateString) {
  const date = new Date(dateString);
  const formatDayOfWeek = timeFormat('%A');
  return formatDayOfWeek(date);
}
