import VueSlider from 'vue-slider-component';
import 'vue-slider-component/theme/default.css';
import { getSingleEndpoint } from "../assets/apiConnector";
import * as d3 from "d3";
export default {
  components: {
    VueSlider
  },
  data() {
    return {
      currentWeekIndex: 0,
      totalWeeks: 0,
      travelJournal: [],
      weekStart: null,
      yScaleMax: 0,
      maxTravelTime: 0,
      svg: null,
      // Aggiunto riferimento all'elemento SVG
      chart: null // Aggiunto riferimento al gruppo chart
    };
  },

  mounted() {
    this.createChart();
  },
  methods: {
    createChart() {
      getSingleEndpoint({
        participantId: 2
      }, "TravelJournal").then(response => {
        this.travelJournal = response.data.map(d => ({
          type: "Feature",
          properties: {
            travelStartTime: d.travelStartTime,
            travelStartLocationId: d.travelStartLocationId,
            travelEndLocationId: d.travelEndLocationId,
            checkInTime: d.checkInTime,
            checkOutTime: d.checkOutTime,
            endingBalance: d.endingBalance,
            travelEndTime: d.travelEndTime,
            participantId: d.participantId,
            purpose: d.purpose,
            startingBalance: d.startingBalance,
            id: d.id
          }
        }));
        const data = response.data;
        const parsedData = data.map(d => {
          const startDate = new Date(d.travelStartTime);
          const endDate = new Date(d.travelEndTime);
          const travelTime = (endDate - startDate) / (1000 * 60);
          return {
            startDate,
            endDate,
            travelTime
          };
        });
        const weekStart = new Date(parsedData[0].startDate.getFullYear(), parsedData[0].startDate.getMonth(), parsedData[0].startDate.getDate() - parsedData[0].startDate.getDay());
        const weekEnd = new Date(parsedData[parsedData.length - 1].startDate.getFullYear(), parsedData[parsedData.length - 1].startDate.getMonth(), parsedData[parsedData.length - 1].startDate.getDate() + 6);
        const totalWeeks = Math.ceil((weekEnd - weekStart) / (1000 * 60 * 60 * 24 * 7));
        this.totalWeeks = totalWeeks;
        this.weekStart = weekStart;
        const margin = {
          top: 20,
          right: 20,
          bottom: 30,
          left: 40
        };
        const width = 800 - margin.left - margin.right;
        const height = 400 - margin.top - margin.bottom;
        this.svg = d3.select(this.$refs.chartContainer).append("svg").attr("width", width + margin.left + margin.right).attr("height", height + margin.top + margin.bottom);
        this.chart = this.svg.append("g").attr("transform", `translate(${margin.left},${margin.top})`);
        this.updateChart(); // Chiamata all'aggiornamento del grafico
      });
    },

    updateChart() {
      const {
        chart,
        svg,
        parsedData,
        maxTravelTime,
        yScaleMax,
        currentWeekIndex,
        weekStart
      } = this;
      const currentWeekStart = new Date(weekStart.getFullYear(), weekStart.getMonth(), weekStart.getDate() + currentWeekIndex * 7);
      const currentWeekEnd = new Date(currentWeekStart.getFullYear(), currentWeekStart.getMonth(), currentWeekStart.getDate() + 6);
      const currentWeekData = parsedData.filter(d => {
        return d.startDate >= currentWeekStart && d.startDate <= currentWeekEnd;
      });
      const weekdays = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
      const totalTravelTimes = weekdays.map(day => {
        const dayTravelTimes = currentWeekData.filter(d => d.startDate.getDay() === weekdays.indexOf(day));
        const totalTravelTime = d3.sum(dayTravelTimes, d => d.travelTime);
        return {
          day,
          totalTravelTime
        };
      });
      const newMaxTravelTime = d3.max(totalTravelTimes, d => d.totalTravelTime);
      if (newMaxTravelTime > maxTravelTime) {
        this.maxTravelTime = newMaxTravelTime;
        this.yScaleMax = maxTravelTime + 30;
      }
      svg.selectAll(".bar").remove();
      svg.selectAll(".x-axis").remove();
      svg.selectAll(".y-axis").remove();
      const x = d3.scaleBand().domain(weekdays).range([0, width]).padding(0.1);
      const yTicks = d3.range(0, yScaleMax, 30);
      const y = d3.scaleLinear().domain([0, maxTravelTime]).range([height, 0]);
      const xAxis = d3.axisBottom(x);
      const yAxis = d3.axisLeft(y).tickValues(yTicks).tickFormat(d => {
        const hours = Math.floor(d / 60);
        const minutes = d % 60;
        return `${hours}:${minutes < 10 ? "0" + minutes : minutes}`;
      });
      chart.append("g").attr("class", "x-axis").attr("transform", `translate(0, ${height})`).call(xAxis);
      chart.append("g").attr("class", "y-axis").call(yAxis);
      chart.selectAll(".bar").data(totalTravelTimes).enter().append("rect").attr("class", "bar").attr("x", d => x(d.day)).attr("y", d => y(d.totalTravelTime)).attr("width", x.bandwidth()).attr("height", d => height - y(d.totalTravelTime));
    }
  }
};
