import * as d3 from "d3";

export function createFinancialHealth(data) {
  const educationLevels = [...new Set(data.map(entry => entry.educationLevel))];
  const monthYears = [...new Set(data.map(entry => entry.monthYear))];

  const margin = { top: 20, right: 20, bottom: 30, left: 50 };
  const width = 700 - margin.left - margin.right;
  const height = 400 - margin.top - margin.bottom;

  // Rimuovi le virgole dal valore dell'asse Y
  const cleanedData = data.map(entry => ({
    ...entry,
    availableBalance: +entry.availableBalance.replace(/,/g, "")
  }));

  const svg = d3.select("#lineChart")
    .append("svg")
    .attr("width", width + margin.left + margin.right)
    .attr("height", height + margin.top + margin.bottom)
    .append("g")
    .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

  const x = d3.scalePoint()
    .domain(monthYears)
    .range([0, width]);

  const y = d3.scaleLinear()
    .domain([0, d3.max(cleanedData, d => d.availableBalance)])
    .range([height, 0]);

  const color = d3.scaleOrdinal()
    .domain(educationLevels)
    .range(d3.schemeCategory10);

  const line = d3.line()
    .x(d => x(d.monthYear))
    .y(d => y(d.availableBalance));

  const tooltip = d3.select("#lineChart").append("div")
    .attr("class", "tooltip")
    .style("opacity", 0);

  educationLevels.forEach(level => {
    const filteredData = cleanedData.filter(entry => entry.educationLevel === level);

    svg.append("path")
      .datum(filteredData)
      .attr("fill", "none")
      .attr("stroke", color(level))
      .attr("stroke-width", 1.5)
      .attr("d", line);

      svg.selectAll(".dot")
      .data(filteredData, (d, i) => level + "-" + i)
      .enter()
      .append("circle")
      .attr("class", "dot-" + level)
      .attr("cx", d => x(d.monthYear))
      .attr("cy", d => y(d.availableBalance))
      .attr("r", 4)
      .attr("fill", color(level))
      .on("mouseover", function(event, d) {
        const tooltipWidth = parseFloat(tooltip.style("width")); // Larghezza del tooltip
        const tooltipHeight = parseFloat(tooltip.style("height")); // Altezza del tooltip
        const xPosition = x(d.monthYear) + margin.left - tooltipWidth / 2; // Posiziona il tooltip al centro del punto x
        const yPosition = event.pageY - tooltipHeight ; // Posiziona il tooltip sopra il mouse sull'asse y

        tooltip.transition()
          .duration(200)
          .style("opacity", 0.9)
          .style("left", xPosition + "px")
          .style("top", yPosition + "px");

        tooltip.html("Available Balance: " + d3.format(",.2f")(d.availableBalance));
      })
      .on("mouseout", function() {
        tooltip.transition()
          .duration(500)
          .style("opacity", 0);
      });

  svg.append("g")
    .attr("transform", "translate(0," + height + ")")
    .call(d3.axisBottom(x));

  svg.append("g")
    .call(d3.axisLeft(y));
  });
}
