import * as d3 from 'd3';

export function createScatterplot(data, container) {
  // set the dimensions and margins of the graph
  const margin = { top: 10, right: 30, bottom: 30, left: 60 };
  const width = 700 - margin.left - margin.right;
  const height = 500 - margin.top - margin.bottom;

  // append the svg object to the container
  const svg = d3.select(container)
    .attr('width', width + margin.left + margin.right)
    .attr('height', height + margin.top + margin.bottom)
    .append('g')
    .attr('transform', `translate(${margin.left}, ${margin.top})`);

  // Get the maximum values for x and y axes
  const maxX = d3.max(data, (d) => +d.hourlyRate);
  const maxY = d3.max(data, (d) => +d.counts);

  // Add X axis
  const x = d3.scaleLinear()
    .domain([0, maxX + 5])
    .range([0, width + 5]);
  svg.append('g')
    .attr('transform', `translate(0, ${height})`)
    .call(d3.axisBottom(x));

  // Add Y axis
  const y = d3.scaleLinear()
    .domain([0, maxY + 5])
    .range([height, 0]);
  svg.append('g')
    .call(d3.axisLeft(y));

  // Add dots
  const dots = svg.selectAll('circle')
    .data(data)
    .enter()
    .append('circle')
    .attr('cx', (d) => x(+d.hourlyRate))
    .attr('cy', (d) => y(+d.counts))
    .attr('r', 5)
    .style('fill', 'steelblue');

  function handleMouseOver(event, d) {
    // Show the jobId details for the corresponding point
    const tooltip = d3.select('.tooltip');
    tooltip.transition().duration(200).style('opacity', 1);

    const svgRect = container.getBoundingClientRect();
    const xPosition = event.clientX - svgRect.left;
    const yPosition = event.clientY - svgRect.top;

    tooltip.html(`Job ID: ${d.jobId}`)
      .style('left', `${xPosition - 35}px`)
      .style('top', `${yPosition + 10}px`); // Adjust the y position

    // Highlight all dots with the same jobId
    const jobId = d.jobId;
    dots.style('fill', (dotData) => (dotData.jobId === jobId ? 'red' : 'steelblue'));
  }

  function handleMouseOut() {
    // Hide the tooltip
    const tooltip = d3.select('.tooltip');
    tooltip.transition().duration(500).style('opacity', 0);

    // Restore the original style of all dots
    dots.style('fill', 'steelblue');
  }

  // Append tooltip element
  const tooltip = d3.select(container)
    .append('div')
    .attr('class', 'tooltip')
    .style('position', 'absolute')
    .style('background-color', 'white')
    .style('border', '1px solid #ddd')
    .style('border-radius', '4px')
    .style('padding', '6px')
    .style('opacity', 0)
    .style('pointer-events', 'none');

  tooltip.append('div')
    .style('position', 'absolute')
    .style('width', '10px')
    .style('height', '10px')
    .style('border-left', '6px solid transparent')
    .style('border-right', '6px solid transparent')
    .style('border-bottom', '6px solid #ddd')
    .style('top', '-6px')
    .style('left', '50%')
    .style('transform', 'translateX(-50%)');

  // Bind event handlers
  dots.on('mouseover', handleMouseOver).on('mouseout', handleMouseOut);
}
