import * as d3 from "d3";

// M A P
function BuildingMap() {
  const color = "#ffffff";
  let extent;

  function me(selection) {
    const buildingTypes = d3
      .groups(selection.datum().map(d => d.properties.buildingType), d => d)
      .map(d => d[0]);
    console.log("buildingTypes", buildingTypes);

    extent = {
      type: "GeometryCollection",
      geometries: selection.datum().map(d => d.geometry)
    };

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const path = d3.geoPath().projection(projection);

    selection
      .selectAll("path")
      .data(selection.datum())
      .join("path")
      .attr("d", path)
      .attr("fill", color)
      .style("stroke", "grey");
  }

  me.color = function () {
    return color;
  };

  me.extent = function() {
    return extent
  };

  return me;
}




function BuildingPub() {
  const colorScale = d3.scaleSequential(d3.interpolateBlues);

  function me(selection, extent) {
    const pubIds = d3
      .groups(selection.datum().map(d => d.properties.location), d => d)
      .map(d => d[0]);

    console.log("pubIds", pubIds);

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const hourlyCostExtent = d3.extent(selection.datum(), d => d.properties.hourlyCost);
    colorScale.domain(hourlyCostExtent);

    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    // Aggiungi un elemento SVG per la legenda
    const legendSvg = selection.append("svg")
      .attr("width", 200)
      .attr("height", 200);

    // Crea intervalli di valori per la legenda
    const minValue = hourlyCostExtent[0];
    const maxValue = hourlyCostExtent[1];
    const range1End = 8;
    const range2End = 10;
    const range3Start = 10;
    const range3End = 12;

    const tickValues = [
      [minValue, range1End],
      [range1End, range2End],
      [range3Start, range3End],
      [range3End, maxValue]
    ];

    // Scala di colori personalizzata
    const customColorScale = d3.scaleQuantize()
      .domain([minValue, maxValue])
      .range(["#fcbba1", "#fc9272", "#fb6a4a", "#de2d26"]);

    // Aggiungi i rettangoli colorati nella legenda
    legendSvg.selectAll(".legend-rect")
      .data(tickValues)
      .enter()
      .append("rect")
      .attr("class", "legend-rect")
      .attr("x", 10)
      .attr("y", (d, i) => 10 + i * 40)
      .attr("width", 20)
      .attr("height", 30)
      .attr("fill", d => customColorScale((d[0] + d[1]) / 2));

    // Aggiungi le etichette per gli intervalli di valori nella legenda
    legendSvg.selectAll(".legend-label")
      .data(tickValues)
      .enter()
      .append("text")
      .attr("class", "legend-label")
      .attr("x", 40)
      .attr("y", (d, i) => 25 + i * 40)
      .text(d => {
        if (d[0] === minValue && d[1] === range1End) {
          return `< ${d[1].toFixed(2)}`;
        } else if (d[0] === range3End && d[1] === maxValue) {
          return `> ${d[0].toFixed(2)}`;
        } else {
          return `${d[0].toFixed(2)} - ${d[1].toFixed(2)}`;
        }
      });

    // Aggiungi i punti sulla mappa
    selection
      .selectAll(".pub")
      .data(selection.datum())
      .join("circle")
      .attr("class", "pub")
      .attr("cx", d => projection(d.geometry.coordinates)[0])
      .attr("cy", d => projection(d.geometry.coordinates)[1])
      .attr("r", 10)
      .attr("fill", d => customColorScale(d.properties.hourlyCost))
      .style("stroke", "black")
      .on("mouseover", (event, d) => {
        const hourlyCost = d.properties.hourlyCost;
        tooltip
          .style("opacity", 1)
          .html(`Hourly cost: $ ${d3.format(".2f")(hourlyCost)}`)
          .style("left", event.pageX + "px")
          .style("top", event.pageY + "px");
      })
      .on("mouseout", () => {
        tooltip.style("opacity", 0);
      });
  }

  return me;
}
















function BuildingRestaurant() {
  const colorScale = d3.scaleDiverging(d3.interpolateReds);

  function me(selection, extent) {
    const restaurantData = selection.datum();

    const restaurantId = d3
      .groups(restaurantData, d => d.properties.restaurantId)
      .map(d => ({
        restaurantId: d[0],
        totalFoodCost: d3.sum(d[1], d => d.properties.foodCost)
      }));

    console.log("restaurantData", restaurantData);
    console.log("restaurantId", restaurantId);

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const foodCostExtent = d3.extent(restaurantData, d => d.properties.foodCost);
    colorScale.domain(foodCostExtent);

    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    selection
      .selectAll(".restaurant")
      .data(restaurantData)
      .join("circle")
      .attr("class", "restaurant")
      .attr("cx", d => projection(d.geometry.coordinates)[0])
      .attr("cy", d => projection(d.geometry.coordinates)[1])
      .attr("r", 10)
      .attr("fill", d => colorScale(d.properties.foodCost))
      .style("stroke", "black")
      .on("mouseover", (event, d) => {
        const foodCost = d.properties.foodCost;
        tooltip
          .style("opacity", 1)
          .html(`Food cost: $ ${d3.format(".2f")(foodCost)}`)
          .style("left", event.pageX + "px")
          .style("top", event.pageY + "px");
      })
      .on("mouseout", () => {
        tooltip.style("opacity", 0);
      });
  }

  return me;
}

export { BuildingMap, BuildingPub, BuildingRestaurant };
