import VueSlider from 'vue-slider-component';
import 'vue-slider-component/theme/default.css';
import { getSingleEndpoint } from "../assets/apiConnector";
import * as d3 from "d3";
export default {
  components: {
    VueSlider
  },
  data() {
    return {
      currentWeekIndex: 0,
      totalWeeks: 0,
      weekStart: null,
      travelJournal: []
    };
  },
  mounted() {
    this.createChart();
  },
  methods: {
    createChart() {
      getSingleEndpoint({
        participantId: 100
      }, "TravelJournal").then(response => {
        this.travelJournal = response.data.map(d => ({
          type: "Feature",
          properties: {
            travelStartTime: d.travelStartTime,
            travelStartLocationId: d.travelStartLocationId,
            travelEndLocationId: d.travelEndLocationId,
            checkInTime: d.checkInTime,
            checkOutTime: d.checkOutTime,
            endingBalance: d.endingBalance,
            travelEndTime: d.travelEndTime,
            participantId: d.participantId,
            purpose: d.purpose,
            startingBalance: d.startingBalance,
            id: d.id
          }
        }));
        const data = response.data;

        // Parsing delle date e calcolo della durata dei viaggi in minuti
        const parsedData = data.map(d => {
          const startDate = new Date(d.travelStartTime);
          const endDate = new Date(d.travelEndTime);
          const travelTime = (endDate - startDate) / (1000 * 60); // Conversione da millisecondi a minuti
          return {
            startDate,
            endDate,
            travelTime
          };
        });

        // Calcola il totale delle settimane
        this.weekStart = new Date(parsedData[0].startDate.getFullYear(), parsedData[0].startDate.getMonth(), parsedData[0].startDate.getDate() - parsedData[0].startDate.getDay());

        // Crea il grafico utilizzando D3
        const margin = {
          top: 20,
          right: 20,
          bottom: 30,
          left: 40
        };
        const width = 800 - margin.left - margin.right;
        const height = 400 - margin.top - margin.bottom;
        const svg = d3.select(this.$refs.chartContainer).append("svg").attr("width", width + margin.left + margin.right).attr("height", height + margin.top + margin.bottom);
        const chart = svg.append("g").attr("transform", `translate(${margin.left},${margin.top})`);

        // Funzione per aggiornare il grafico in base alla settimana selezionata
        const updateChart = () => {
          const currentWeekStart = new Date(this.weekStart.getFullYear(), this.weekStart.getMonth(), this.weekStart.getDate() + this.currentWeekIndex * 7);
          const currentWeekEnd = new Date(currentWeekStart.getFullYear(), currentWeekStart.getMonth(), currentWeekStart.getDate() + 6);
          const currentWeekData = parsedData.filter(d => {
            return d.startDate >= currentWeekStart && d.startDate <= currentWeekEnd;
          });

          // Calcola il totale della differenza tra startDate e endDate per ogni giorno della settimana
          const weekdays = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
          const totalTravelTimes = weekdays.map(day => {
            const dayTravelTimes = currentWeekData.filter(d => d.startDate.getDay() === weekdays.indexOf(day));
            const totalTravelTime = d3.sum(dayTravelTimes, d => d.travelTime);
            return {
              day,
              totalTravelTime
            };
          });

          // Rimuovi il vecchio grafico
          svg.selectAll(".bar").remove();
          svg.selectAll(".x-axis").remove();
          svg.selectAll(".y-axis").remove();

          // Scala per gli assi x e y
          const x = d3.scaleBand().domain(weekdays).range([0, width]).padding(0.1);
          const yTicks = d3.range(0, d3.max(totalTravelTimes, d => d.totalTravelTime) + 600, 600); // Genera intervalli di 10 ore (600 minuti) per l'asse y
          const y = d3.scaleLinear().domain([0, d3.max(totalTravelTimes, d => d.totalTravelTime) + 600]).range([height, 0]);

          // Creazione degli assi x e y
          const xAxis = d3.axisBottom(x);
          const yAxis = d3.axisLeft(y).tickValues(yTicks).tickFormat(d => {
            const hours = Math.floor(d / 60); // Calcola le ore
            return `${hours}`; // Formatta l'etichetta come "hh:00"
          });

          // Aggiungi gli assi al grafico
          chart.append("g").attr("class", "x-axis").attr("transform", `translate(0, ${height})`).call(xAxis);
          chart.append("g").attr("class", "y-axis").call(yAxis);

          // Crea le barre del grafico
          chart.selectAll(".bar").data(totalTravelTimes).enter().append("rect").attr("class", "bar").attr("x", d => x(d.day)).attr("y", d => y(d.totalTravelTime)).attr("width", x.bandwidth()).attr("height", d => height - y(d.totalTravelTime));
        };

        // Aggiorna il grafico iniziale
        updateChart();

        // Aggiorna il grafico quando viene cambiata la settimana selezionata
        this.$watch("currentWeekIndex", () => {
          updateChart();
        });
      });
    },
    formatWeekLabel(value) {
      const currentWeekStart = new Date(this.weekStart.getFullYear(), this.weekStart.getMonth(), this.weekStart.getDate() + value * 7);
      const currentWeekEnd = new Date(currentWeekStart.getFullYear(), currentWeekStart.getMonth(), currentWeekStart.getDate() + 6);
      return `${currentWeekStart.toDateString()} - ${currentWeekEnd.toDateString()}`;
    },
    getCurrentWeekLabel() {
      const currentWeekStart = new Date(this.weekStart.getFullYear(), this.weekStart.getMonth(), this.weekStart.getDate() + this.currentWeekIndex * 7);
      const currentWeekEnd = new Date(currentWeekStart.getFullYear(), currentWeekStart.getMonth(), currentWeekStart.getDate() + 6);
      return `${currentWeekStart.toDateString()} - ${currentWeekEnd.toDateString()}`;
    }
  }
};
