import "core-js/modules/es.array.push.js";
import { getSingleEndpoint } from "../assets/apiConnector";
import * as d3 from "d3";
export default {
  data() {
    return {
      participantStatusLogs: [],
      heatmapData: []
    };
  },
  mounted() {
    this.getParticipantStatusLogs().then(() => {
      this.initializeHeatmap();
    });
  },
  methods: {
    async getParticipantStatusLogs() {
      try {
        const response = await getSingleEndpoint({
          participantId: 1
        }, 'ParticipantStatusLogs');
        this.participantStatusLogs = response.data.map(d => ({
          type: "Feature",
          properties: {
            timestamp: d.timestamp,
            currentMode: d.currentMode
          }
        }));
      } catch (error) {
        console.error('Error accessing data:', error);
      }
    },
    initializeHeatmap() {
      const discretizedTimestamps = this.discretizeTimestamps(this.participantStatusLogs);
      const groupedData = this.groupDataByDay(this.participantStatusLogs);
      this.heatmapData = this.createHeatmapData(discretizedTimestamps, groupedData);
    },
    discretizeTimestamps(data) {
      const timestamps = data.map(item => item.properties.timestamp);
      const sortedTimestamps = timestamps.sort((a, b) => a - b);
      const startTimestamp = sortedTimestamps[0];
      const endTimestamp = sortedTimestamps[sortedTimestamps.length - 1];
      const interval = 3 * 60 * 60 * 1000; // Intervallo di 3 ore in millisecondi

      const discretizedTimestamps = [];
      for (let timestamp = startTimestamp; timestamp <= endTimestamp; timestamp += interval) {
        discretizedTimestamps.push(timestamp);
      }
      return discretizedTimestamps;
    },
    groupDataByDay(data) {
      const groupedData = {};
      data.forEach(item => {
        const date = this.getDateString(item.properties.timestamp);
        if (!groupedData[date]) {
          groupedData[date] = [];
        }
        groupedData[date].push(item);
      });
      return groupedData;
    },
    getDateString(timestamp) {
      const dateObj = new Date(timestamp);
      const year = dateObj.getFullYear();
      const month = this.addLeadingZero(dateObj.getMonth() + 1);
      const day = this.addLeadingZero(dateObj.getDate());
      return `${year}-${month}-${day}`;
    },
    addLeadingZero(value) {
      return value < 10 ? `0${value}` : value.toString();
    },
    createHeatmapData(timestamps, groupedData) {
      const heatmapData = [];
      for (const date in groupedData) {
        const dayData = groupedData[date];
        const rowData = [];
        for (let i = 0; i < timestamps.length - 1; i++) {
          const intervalStart = timestamps[i];
          const intervalEnd = timestamps[i + 1];
          const modeChange = this.checkModeChange(dayData, intervalStart, intervalEnd);
          const color = modeChange ? this.getColor(dayData, intervalStart) : '';
          rowData.push(color);
        }
        heatmapData.push(rowData);
      }
      return heatmapData;
    },
    checkModeChange(data, start, end) {
      for (const item of data) {
        if (item.properties.timestamp >= start && item.properties.timestamp < end) {
          if (item.properties.currentMode !== data[0].properties.currentMode) {
            return true;
          }
        }
      }
      return false;
    },
    getColor(data, timestamp) {
      const uniqueModes = [...new Set(data.map(item => item.properties.currentMode))];
      const colorScale = d3.scaleOrdinal().domain(uniqueModes).range(d3.schemeCategory10);
      for (const item of data) {
        if (item.properties.timestamp === timestamp) {
          return colorScale(item.properties.currentMode);
        }
      }
      return '';
    },
    getSquareClass(square) {
      return square ? `square ${square}` : 'square';
    }
  }
};
