import { getSingleEndpoint } from "../assets/apiConnector";
import * as d3 from "d3";
import VueSlider from 'vue-slider-component';
import 'vue-slider-component/theme/default.css';
export default {
  components: {
    VueSlider
  },
  data() {
    return {
      currentWeekIndex: 0,
      travelJournal: [],
      weekStart: new Date()
    };
  },
  mounted() {
    this.createChart();
  },
  methods: {
    createChart() {
      getSingleEndpoint({
        participantId: 1010
      }, 'TravelJournal').then(response => {
        this.travelJournal = response.data.map(d => ({
          type: "Feature",
          properties: {
            travelStartTime: d.travelStartTime,
            travelStartLocationId: d.travelStartLocationId,
            travelEndLocationId: d.travelEndLocationId,
            checkInTime: d.checkInTime,
            checkOutTime: d.checkOutTime,
            endingBalance: d.endingBalance,
            travelEndTime: d.travelEndTime,
            participantId: d.participantId,
            purpose: d.purpose,
            startingBalance: d.startingBalance,
            id: d.id
          }
        }));
        const data = response.data;

        // Parsing delle date e calcolo della durata dei viaggi in minuti
        const parsedData = data.map(d => {
          const startDate = new Date(d.travelStartTime);
          const endDate = new Date(d.travelEndTime);
          const travelTime = (endDate - startDate) / (1000 * 60); // Conversione da millisecondi a minuti
          return {
            startDate,
            endDate,
            travelTime
          };
        });

        // Crea il grafico utilizzando D3
        const margin = {
          top: 20,
          right: 20,
          bottom: 30,
          left: 40
        };
        const width = 800 - margin.left - margin.right;
        const height = 400 - margin.top - margin.bottom;
        const svg = d3.select(this.$refs.chartContainer).append("svg").attr("width", width + margin.left + margin.right).attr("height", height + margin.top + margin.bottom);
        const chart = svg.append("g").attr("transform", `translate(${margin.left},${margin.top})`);
        const updateChart = () => {
          const weekStart = new Date(this.weekStart);
          weekStart.setDate(weekStart.getDate() + this.currentWeekIndex * 7);
          const weekEnd = new Date(weekStart);
          weekEnd.setDate(weekEnd.getDate() + 6);
          const weekStartStr = weekStart.toLocaleDateString('en-US', {
            day: 'numeric',
            month: 'long'
          });
          const weekEndStr = weekEnd.toLocaleDateString('en-US', {
            day: 'numeric',
            month: 'long'
          });
          const weekInfo = `${weekStartStr}-${weekEndStr}`;
          d3.select("#week-info").text(weekInfo);
          const filteredData = parsedData.filter(d => {
            const startDate = new Date(d.startDate);
            return startDate >= weekStart && startDate <= weekEnd;
          });
          const totalTravelTimes = weekdays.map(day => {
            const dayTravelTimes = filteredData.filter(d => d.startDate.getDay() === weekdays.indexOf(day));
            const totalTravelTime = d3.sum(dayTravelTimes, d => d.travelTime);
            return {
              day,
              totalTravelTime
            };
          });
          const x = d3.scaleBand().domain(weekdays).range([0, width]).padding(0.1);
          const yTicks = d3.range(0, d3.max(totalTravelTimes, d => d.totalTravelTime) + 30, 30);
          const y = d3.scaleLinear().domain([0, d3.max(totalTravelTimes, d => d.totalTravelTime) + 30]).range([height, 0]);
          const xAxis = d3.axisBottom(x);
          const yAxis = d3.axisLeft(y).tickValues(yTicks).tickFormat(d => {
            const hours = Math.floor(d / 60);
            return `${hours}:00`;
          });
          chart.select(".x-axis").call(xAxis);
          chart.select(".y-axis").call(yAxis);
          const bars = chart.selectAll(".bar").data(totalTravelTimes);
          bars.enter().append("rect").attr("class", "bar").merge(bars).attr("x", d => x(d.day)).attr("y", d => y(d.totalTravelTime)).attr("width", x.bandwidth()).attr("height", d => height - y(d.totalTravelTime));
          bars.exit().remove();
        };
        const weekdays = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
        svg.append("text").attr("id", "week-info").attr("x", margin.left).attr("y", margin.top).text("");
        updateChart();
      });
    },
    updateChart() {
      this.weekStart = new Date();
      this.weekStart.setDate(this.weekStart.getDate() - this.currentWeekIndex * 7);
      this.createChart();
    }
  }
};
