import * as d3 from "d3";

// M A P
function BuildingMap() {
  const color = "#ffffff";
  let extent;

  function me(selection) {
    const buildingTypes = d3
      .groups(selection.datum().map(d => d.properties.buildingType), d => d)
      .map(d => d[0]);
    console.log("buildingTypes", buildingTypes);

    extent = {
      type: "GeometryCollection",
      geometries: selection.datum().map(d => d.geometry)
    };

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const path = d3.geoPath().projection(projection);

    selection
      .selectAll("path")
      .data(selection.datum())
      .join("path")
      .attr("d", path)
      .attr("fill", color)
      .style("stroke", "black");
  }

  me.color = function () {
    return color;
  };

  me.extent = function() {
    return extent
  };

  return me;
}

// A P A R T M E N T S
function BuildingApt() {
    function me(selection, extent) {
      const apartmentIds = d3
        .groups(selection.datum().map(d => d.properties.apartmentId), d => d)
        .map(d => d[0]);

      console.log("apartmentIds", apartmentIds);

      const projection = d3
        .geoIdentity()
        .reflectY(true)
        .fitSize([650, 650], extent);

      selection
        .selectAll(".apt")
        .data(selection.datum())
        .join("circle")
        .attr("class", "apt")
        .attr("cx", d => projection(d.geometry.coordinates)[0])
        .attr("cy", d => projection(d.geometry.coordinates)[1])
        .attr("r", 1);
    }

    return me;
  }

// P U B S
function BuildingPub() {
  const colorScale = d3.scaleDiverging(d3.interpolateBlues);
  let pubIdsData;

  function me(selection, extent) {
    const pubData = selection.datum();

    const pubIds = Array.from(new Set(pubData.map(d => d.properties.location)));

    pubIdsData = pubIds.map(pubId => {
      const filteredData = pubData.filter(d => d.properties.location === pubId);
      const totalHourlyCost = d3.sum(filteredData, d => d.properties.hourlyCost);
      return {
        pubId,
        totalHourlyCost
      };
    });

    console.log("pubData", pubData);
    console.log("pubIdsData", pubIdsData);

    console.log("pubData", pubData);
    console.log("pubIdsData", pubIdsData);

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const hourlyCostExtent = d3.extent(pubData, d => d.properties.hourlyCost);
    colorScale.domain(hourlyCostExtent);

    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    selection
      .selectAll(".pub")
      .data(pubData)
      .join("circle")
      .attr("class", "pub")
      .attr("cx", d => projection(d.geometry.coordinates)[0])
      .attr("cy", d => projection(d.geometry.coordinates)[1])
      .attr("r", 10)
      .attr("fill", d => colorScale(d.properties.hourlyCost))
      .style("stroke", "black")
      .on("mouseover", (event, d) => {
        const pubIdData = pubIdsData.find(data => data.location === d.properties.location);
        const totalHourlyCost = pubIdData ? pubIdData.totalHourlyCost : 0;
        tooltip
          .style("opacity", 1)
          .html(`Total Hourly Cost: ${d3.format(".2f")(totalHourlyCost)}`)
          .style("left", event.pageX + "px")
          .style("top", event.pageY + "px");
      })
      .on("mouseout", () => {
        tooltip.style("opacity", 0);
      });
  }

  return me;
}

// R E S T A U R A N T S
function BuildingRestaurant() {
  const colorScale = d3.scaleDiverging(d3.interpolateReds);
  let restaurantIdsData;

  function me(selection, extent) {
    const restaurantData = selection.datum();

    const restaurantRollup = d3.rollup(
      restaurantData,
      group => d3.sum(group, d => d.properties.foodCost),
      d => d.properties.restaurantId
    );

    restaurantIdsData = Array.from(restaurantRollup, ([restaurantId, totalFoodCost]) => ({
      restaurantId,
      totalFoodCost
    }));

    console.log("restaurantData", restaurantData);
    console.log("restaurantIdsData", restaurantIdsData);

    const projection = d3
      .geoIdentity()
      .reflectY(true)
      .fitSize([650, 650], extent);

    const foodCostExtent = d3.extent(restaurantData, d => d.properties.foodCost);
    colorScale.domain(foodCostExtent);

    const tooltip = d3
      .select("body")
      .append("div")
      .attr("class", "tooltip")
      .style("opacity", 0);

    selection
      .selectAll(".restaurant")
      .data(restaurantData)
      .join("circle")
      .attr("class", "restaurant")
      .attr("cx", d => projection(d.geometry.coordinates)[0])
      .attr("cy", d => projection(d.geometry.coordinates)[1])
      .attr("r", 10)
      .attr("fill", d => colorScale(d.properties.foodCost))
      .style("stroke", "black")
      .on("mouseover", (event, d) => {
        const restaurantIdData = restaurantIdsData.find(data => data.restaurantId === d.properties.restaurantId);
        const totalFoodCost = restaurantIdData ? restaurantIdData.totalFoodCost : 0;
        tooltip
          .style("opacity", 1)
          .html(`Total Food Cost: ${d3.format(".2f")(totalFoodCost)}`)
          .style("left", event.pageX + "px")
          .style("top", event.pageY + "px");
      })
      .on("mouseout", () => {
        tooltip.style("opacity", 0);
      });
  }

  return me;
}




export { BuildingMap, BuildingApt, BuildingPub, BuildingRestaurant };
