import * as d3 from 'd3';
import { format } from 'date-fns';

export function drawChart(data, chartContainer) {
  const margin = { top: 20, right: 20, bottom: 30, left: 40 };
  const width = 600 - margin.left - margin.right;
  const height = 400 - margin.top - margin.bottom;

  const maxTravelDuration = d3.max(data, (d) => +d.travelDuration);

  const svg = d3
    .select(chartContainer)
    .append('svg')
    .attr('width', width + margin.left + margin.right)
    .attr('height', height + margin.top + margin.bottom)
    .append('g')
    .attr('transform', `translate(${margin.left},${margin.top})`);

  const xScale = d3
    .scaleBand()
    .range([0, width])
    .padding(0.1)
    .domain(data.map((d) => getFormattedDate(d.date))); // Mappa le date ai giorni della settimana formattati

  const yScale = d3
    .scaleLinear()
    .range([height, 0])
    .domain([0, maxTravelDuration]);

  svg
    .append('g')
    .attr('class', 'x-axis')
    .attr('transform', `translate(0,${height})`)
    .call(d3.axisBottom(xScale));

  svg.append('g').attr('class', 'y-axis').call(d3.axisLeft(yScale).tickFormat(d3.format('.2s')).ticks(5));

  svg
    .selectAll('.bar')
    .data(data)
    .enter()
    .append('rect')
    .attr('class', 'bar')
    .attr('x', (d) => xScale(getFormattedDate(d.date))) // Utilizza la data formattata come posizione x
    .attr('y', (d) => yScale(+d.travelDuration))
    .attr('width', xScale.bandwidth())
    .attr('height', (d) => height - yScale(+d.travelDuration));
}

export function updateChart(data, selectedWeek, chartContainer) {
  const weekData = data.slice(selectedWeek * 7, selectedWeek * 7 + 7);
  const svg = d3.select(chartContainer).select('svg');

  const margin = { top: 20, right: 20, bottom: 30, left: 40 };
  const width = svg.attr('width') - margin.left - margin.right;
  const height = svg.attr('height') - margin.top - margin.bottom;

  const xScale = d3
    .scaleBand()
    .range([0, width])
    .padding(0.1)
    .domain(weekData.map((d) => getFormattedDate(d.date))); // Mappa le date ai giorni della settimana formattati

  const yScale = d3
    .scaleLinear()
    .range([height, 0])
    .domain([0, d3.max(data, (d) => +d.travelDuration)]);

  svg.select('.x-axis').call(d3.axisBottom(xScale));
  svg.select('.y-axis').call(d3.axisLeft(yScale).tickFormat(d3.format('.2s')).ticks(5));

  const bars = svg.selectAll('.bar').data(weekData, (d) => d.date);

  bars.exit().remove();

  bars
    .enter()
    .append('rect')
    .attr('class', 'bar')
    .merge(bars)
    .attr('x', (d) => margin.left + xScale(getFormattedDate(d.date))) // Utilizza la data formattata come posizione x
    .attr('y', (d) => margin.top + yScale(+d.travelDuration))
    .attr('width', xScale.bandwidth())
    .attr('height', (d) => height - yScale(+d.travelDuration));
}

// Funzione per ottenere la data formattata come "giorno mese anno"
function getFormattedDate(dateString) {
  const date = new Date(dateString);
  return format(date, 'dd MMMM yyyy');
}
