import "core-js/modules/es.array.push.js";
import { getSingleEndpoint } from "../assets/apiConnector";
let Buffer = require("buffer/").Buffer;
const wkx = require("wkx");
const d3 = require("d3");
import { BuildingMap, BuildingTypeLegend, BuildingApt, BuildingPub, BuildingRestaurant } from "@/assets/Map";
const bm = new BuildingMap();
const bl = new BuildingTypeLegend();
const apt = new BuildingApt();
const pub = new BuildingPub();
const rest = new BuildingRestaurant();
export default {
  name: "App",
  components: {},
  data() {
    return {
      cards: [{
        title: "Question 1",
        description: "Assuming the volunteers are representative of the city’s population, characterize the distinct areas of the city that you identify. For each area you identify, provide your rationale and supporting data.",
        body: `
            <div class="row justify-content-center">
              <div class="col-md-8">
                <div class="button-container">
                  <button :class="{ active: pubsActive }" @click="togglePubs" class="pubsButton">Pubs</button>
                  <button :class="{ active: restaurantsActive }" @click="toggleRestaurants" class="restButton">Restaurants</button>
                </div>
              </div>
            </div>
            <div class="map-container">
              <svg width="100%" height="700px" ref="map">
                <g class="buildings"></g>
                <g class="legend"></g>
                <g class="apartments"></g>
                <g class="pubs" :opacity="pubsActive ? 1 : 0"></g>
                <g class="restaurants" :opacity="restaurantsActive ? 1 : 0"></g>
              </svg>
            </div>
          `
      }, {
        title: "Question 2",
        description: "Summarize the patterns of life you observed for each area, both in terms of volume and diversity of people and their movement. Did you see evidence of distinct subgroups?",
        body: "Question 2 body content goes here."
      }, {
        title: "Question 3",
        description: "Are there patterns of movement that appear to indicate bottlenecks or obstacles to travel? What changes might you propose to reduce congestion?",
        body: "Question 3 body content goes here."
      }, {
        title: "Question 4",
        description: "Are there hazards, either environmental or social, that you identified? How might these be mitigated?",
        body: "Question 4 body content goes here."
      }, {
        title: "Question 5",
        description: "Are there patterns that change over time? Over seasons? How might your proposed changes from questions 3 and 4 be impacted by time and/or season?",
        body: "Question 5 body content goes here."
      }],
      collapsedCards: [],
      buildings: [],
      apartments: [],
      pubs: [],
      restaurants: [],
      pubsActive: false,
      restaurantsActive: false,
      collapse1: false
    };
  },
  mounted() {
    // DRAW THE MAP
    getSingleEndpoint({}, "Buildings").then(response => {
      this.buildings = response.data.map(d => ({
        type: "Feature",
        geometry: wkx.Geometry.parse(Buffer.from(d.location, "hex")).toGeoJSON(),
        properties: {
          buildingId: d.buildingId,
          buildingType: d.buildingType,
          maxOccupancy: d.maxOccupancy,
          units: d.units
        }
      }));
      // DRAW APARTMENTS
      getSingleEndpoint({}, "Apartments").then(response => {
        this.apartments = response.data.map(d => ({
          type: "Feature",
          geometry: wkx.Geometry.parse(Buffer.from(d.location, "hex")).toGeoJSON(),
          properties: {
            numberOfRooms: d.numberOfRooms,
            apartmentId: d.apartmentId,
            buildingId: d.buildingId,
            rentalCost: d.rentalCost,
            maxOccupancy: d.maxOccupancy
          }
        }));
        this.drawApt();
      });
      // DRAW PUBS
      getSingleEndpoint({}, "Pubs").then(response => {
        this.pubs = response.data.map(d => ({
          type: "Feature",
          geometry: wkx.Geometry.parse(Buffer.from(d.location, "hex")).toGeoJSON(),
          properties: {
            pubId: d.pubId,
            hourlyCost: d.hourlyCost,
            maxOccupancy: d.maxOccupancy,
            buildingId: d.buildingId
          }
        }));
        this.drawPub();
      });
      // DRAW RESTAURANTS
      getSingleEndpoint({}, "Restaurants").then(response => {
        this.restaurants = response.data.map(d => ({
          type: "Feature",
          geometry: wkx.Geometry.parse(Buffer.from(d.location, "hex")).toGeoJSON(),
          properties: {
            restaurantId: d.restaurantId,
            maxOccupancy: d.maxOccupancy,
            foodCost: d.foodCost,
            buildingId: d.buildingId
          }
        }));
        this.drawRestaurants();
      });
      this.drawBuildings();
    });
  },
  methods: {
    drawBuildings() {
      d3.select(this.$refs.map).select("g.buildings").datum(this.buildings).call(bm);
      const colorScale = bm.color().domain().concat("apartments");
      bl.color(d3.scaleOrdinal(d3.schemeCategory10).domain(colorScale));
      d3.select(this.$refs.map).select("g.legend").datum(colorScale).call(bl);
    },
    drawApt() {
      d3.select(this.$refs.map).select("g.apartments").datum(this.apartments).call(apt);
    },
    drawPub() {
      d3.select(this.$refs.map).select("g.pubs").datum(this.pubs).call(pub);
    },
    drawRestaurants() {
      d3.select(this.$refs.map).select("g.restaurants").datum(this.restaurants).call(rest);
    },
    togglePubs() {
      this.pubsActive = !this.pubsActive;
    },
    toggleRestaurants() {
      this.restaurantsActive = !this.restaurantsActive;
    },
    toggleCollapse(index) {
      if (this.isCollapsed(index)) {
        const indexToRemove = this.collapsedCards.indexOf(index);
        this.collapsedCards.splice(indexToRemove, 1);
      } else {
        this.collapsedCards.push(index);
      }
    },
    isCollapsed(index) {
      return this.collapsedCards.includes(index);
    }
  }
};
